import React, { useState, useEffect } from 'react';
import './AdminPage.css';
import axios from 'axios';
import { Navigate, BrowserRouter as Router, Route, Routes , useLocation } from 'react-router-dom'; // Import Navigate instead of Redirect

import withAuth from './withAuth'; // Import the withAuth HOC
import ListUsers from './ListUsers';
import CreateUser from './CreateUsers';

const AdminPage = () => {
   // Initialize users as null
  const [tokenExists, setTokenExists] = useState(true);

  const [id, setSearchId] = useState('');
  const [formData, setFormData] = useState({
    username: "",
    first_name: "",
    last_name: "",
    email: "",
    phone: "",
    office: "",
    role: "",
    password: "",
    confirm_password: "",
    gender: "",
    is_active: false,
  });
  const [errors, setErrors] = useState({});

  const [submenuVisible, setSubmenuVisible] = useState(false);


  useEffect(() => {
    const storedTokenString = localStorage.getItem('token');
    if (!storedTokenString) {
      setTokenExists(false);
      return;
    }

    const token = JSON.parse(storedTokenString);
    console.log('Stored Token:', token);

    // Check if token has expired
    const currentTime = Date.now() / 1000; // Convert milliseconds to seconds
    if (token.expires && token.expires < currentTime) {
      // Token has expired, clear it from local storage and redirect to login page
      localStorage.removeItem('token');
      setTokenExists(false);
      return;
    }

    // Token is valid, don't fetch users initially
  }, []);




  //const handleMenuItemClick = async () => {
    //setSubmenuVisible(!submenuVisible);
    //await fetchUsers();
  //};




 // const handleDeleteClick = async () => {
   // await fetchUsers();
  //};

  if (!tokenExists) {
    return <Navigate to="/" />;
  }





  // CREATING USER

  const handleChange = (e) => {
    const { type, name, value, checked } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };



  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validate form data
    const validationErrors = {};

    // Validate username
    if (!formData.username.trim()) {
      validationErrors.username = 'Username is required';
    } else if (!/^[a-zA-Z0-9@.+_-]{1,150}$/.test(formData.username.trim())) {
      validationErrors.username = 'Username must be 150 characters or fewer and contain only letters, digits, and @/./+/-/_';
    }

    // Validate email
    if (!formData.email.trim()) {
      validationErrors.email = 'Email address is required';
    } else if (!/^\S+@\S+\.\S+$/.test(formData.email.trim())) {
      validationErrors.email = 'Invalid email address';
    }
    // Validate password
    if (!formData.password.trim()) {
      validationErrors.password = 'Password is required';
    } else if (formData.password.trim() !== formData.confirm_password.trim()) {
      validationErrors.confirm_password = 'Passwords do not match';
    }

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    console.log('Form Data:', formData);
    // Submit form data if validation passed
    try {
      console.log('Form Data:', formData);
      const storedTokenString = localStorage.getItem('token');
      const token = JSON.parse(storedTokenString);


      await axios.post(`http://0.0.0.0:8888/users/`, formData, {
        headers: {

          Authorization: `Token ${token.key}` 
        }
      });

      console.log('Error fetching users:');
      setFormData({
        username: "",
        first_name: "",
        last_name: "",
        email: "",
        phone: "",
        office: "",
        role: "",
        password: "",
        confirm_password: "",
        gender: "",
        is_active: false,
      });
      setErrors({});
      alert('User created successfully');
    } catch (error) {

      console.error('Error creating user:', error);
      alert('Failed to create user. Please try again.');

    }















  };


  return (
