import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useHistory } from 'react-router-dom';


import './Login.css';
{/*import { FaUser,FaLock} from "react-icons/fa";*/}

const Login = () => {
  const [username, setusername] = useState('');
  const [password, setPass] = useState('');
  const navigate = useNavigate();

  /*const handleSubmit = (e) => {
    e.preventDefault();
    console.log(email);
  }*/

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://0.0.0.0:8888/auth/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          'username': username,
          'password': password,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        const { role } = data;

        if (role === 'Administrator') {
          history.push('/admin');
        } else if(role === 'Staff'){
          navigate.push('/user1');
        } else if(role === 'Assistant') {
          navigate.push('/user2')
        }
      } else {

        console.error('Login failed');
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };













  return (
    <div className='wrapper'>

        <form onSubmit={handleSubmit}>
            <h1>Login</h1>

            <div className='input-box'>
                <input value={username} onChange={(e) => setusername(e.target.value)} type='text' placeholder='UserName' required />
                {/*<FaUser className='icon' />*/}
            </div>

            <div className='input-box'>
                <input value={password} onChange={(e) => setPass(e.target.value)} type='password' placeholder='Password' required />
                {/*<FaLock className='icon' />*/}
            </div>

            <div className="remember-forgot">
              <label><input type='checkbox'/>Remember me </label>
              <a href='#'> Forgot password</a>
            </div>

            <button type='submit'>Login</button>

            {/*<div className='register-link'>
              <p>Don't have an account? <a onClick={() => props.onFormSwitch('register')} href='#'>Register</a></p>
  </div>*/}
        </form>

    </div>
  )
}

export default Login
