import React, { useState, useEffect } from 'react';
import './AdminPage.css';
import axios from 'axios';

const AdminPage = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    const token = JSON.parse(storedToken);
    console.log('Stored Token:', token); // Log the token to verify its correctness

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://0.0.0.0:8888/users/', {
          headers: {
            Authorization: token // Include token in request headers
          }
        });

        // Assuming the backend returns the user role along with the user list
        const { role, userList } = response.data;

        // Check if the user has admin privileges
        if (role === 'Administrator') {
          setUsers(userList);
        } else {
          console.error('User is not authorized to access the user list.');
        }
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  return (
    <div>
      <div className="sidebar">
        <ul>
          <li>Menu Item 1</li>
          <li>Menu Item 2</li>
          <li>Menu Item 3</li>
        </ul>
      </div>
      <div>
        <h2>Admin Panel</h2>
        <p>Welcome to the Admin Panel. You have access to administrative features.</p>
      </div>

      <div>
        <ul>
          {users.map(user => (
            <li key={user.id}>{user.username} - {user.email}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminPage;
