import React, { useState, useEffect } from 'react';
import './AdminPage.css';
import axios from 'axios';

const AdminPage = () => {
  const [users, setUsers] = useState([]);

  const fetchUsers = async () => {
    try {
      const storedToken = localStorage.getItem('token');
      const token = JSON.parse(storedToken);

      const response = await axios.get('http://0.0.0.0:8888/users/', {
        headers: {
          Authorization: `Bearer ${token}` // Include token in request headers
        }
      });
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    // Fetch users initially when the component mounts
    fetchUsers();
  }, []);

  const handleMenuItemClick = () => {
    // Fetch users when Menu Item 1 is clicked
    fetchUsers();
  };

  return (
    <div>
      <div className="sidebar">
        <ul>

          <li onClicked={handleMenuItemClick}>Menu Item 1</li>
          <li>Menu Item 2</li>
          <li>Menu Item 3</li>
        </ul>
      </div>
      <div>
        <h2>Admin Panel</h2>
        <p>Welcome to the Admin Panel. You have access to administrative features.</p>
      </div>

      <div>
        <ul>
          {users.map(user => (
            <li key={user.id}>{user.username} - {user.email}</li> 
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminPage;
