import React, { useState } from 'react';
import './AdminPage.css';
import axios from 'axios';
import { Navigate } from 'react-router-dom'; // Import Navigate instead of Redirect

const AdminPage = () => {
  const [users, setUsers] = useState([]);
  const [tokenExists, setTokenExists] = useState(true);

  const fetchUsers = async () => {
    try {
      const storedTokenString = localStorage.getItem('token');
      if (!storedTokenString) {
        setTokenExists(false);
        return;
      }

      const token = JSON.parse(storedTokenString);
      console.log('Stored Token:', token);

      const response = await axios.get('http://0.0.0.0:8888/users/', {
        headers: {
          Authorization: `Token ${token.key}` 
        }
      });
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleMenuItemClick = async () => {
    fetchUsers();
  };

  if (!tokenExists) {
    Navigate('/'); // Redirect to the login page if token does not exist
    return null; // Return null to prevent rendering the AdminPage component
  }

  return (
    <div>
      <div className="sidebar">
        <ul>
          <li onClick={handleMenuItemClick}>Menu Item 1</li>
          <li>Menu Item 2</li>
          <li>Menu Item 3</li>
        </ul>
      </div>
      <div>
        <h2>Admin Panel</h2>
        <p>Welcome to the Admin Panel. You have access to administrative features.</p>
      </div>

      <div>
        <ul>
          {users.map(user => (
            <li key={user.id}>{user.username} - {user.email}</li> 
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminPage;
